import React from "react";
import { connect } from "react-redux";

import { selectCategory } from "../action/Action";
function Selectpage(props) {
  function onClickHandler(categoryName) {
    props.selectCategory(categoryName);
  }

  return (
    <>
      <h2>select catagories</h2>
      {props.categories.map((item) => (
        <button
          onClick={() => {
            onClickHandler(item.normalizedName);
          }}
        >
          {item.normalizedName}
        </button>
      ))}
    </>
  );
}
const mapStateToProps = (state) => {
  console.log("STATE============>", state);
  return state.item;
};
const mapDispatchToProps = { selectCategory };
export default connect(mapStateToProps, mapDispatchToProps)(Selectpage);
// export default Selectpage;
